            </li>
            <li>
              <NavLink
                className="projects"
                to="/projects"
                tag={Link}
                activeStyle={{ boxShadow: '0px 18px 11px -12px rgba(44, 187, 253, 0.904)', transform: 'translateY(-0.3em)' }}
                style={{ borderRadius: 10, color: theme.text }}
              >
                Projects
              </NavLink>
            </li>
            <li>
              <NavLink
                className="cr"
                to="/contact"
                tag={Link}
                activeStyle={{ boxShadow: '0px 18px 11px -12px rgba(44, 187, 253, 0.904)', transform: 'translateY(-0.3em)' }}
                style={{ borderRadius: 10, color: theme.text }}
              >
                Contact and Resume
              </NavLink>
            </li>
            {/* <button {...styles} onClick={changeTheme}>
              {icon}
            </button> */}
          </ul>
        </header>
      </div>
    </Fade>
  );
}

export default Header;
